public class RightTraingleVShape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//row
		for(int i=0;i<=3;i++) {
			//column
			for(int j=0 ;j<=3;j++) {
				System.out.print("V");
			}
			
			System.out.println();
		}
	}

}
