package dsa_18th_June_2022;

public class VivekCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		for (int i = 1; i <= 5; i++) {
			for (int j = 1; j <= i; j++) {
				System.out.print(a * a + " ");
				a++;

			}
			System.out.println();
		}

	}

}
